import React from 'react'
import { EnhancedMovie } from '../types'
import { Image, Badge, Card, List } from 'antd'
import styled from 'styled-components'
import { ActionButtons } from './ActionButtons'
import dayjs from 'dayjs'

interface MovieListProps {
  title?: string
  movies: EnhancedMovie[]
  onAdd?: (movie: EnhancedMovie) => void
  onChange?: (movie: EnhancedMovie) => void
  onDelete?: (id: EnhancedMovie['id']) => void
  pagination?: {
    onChange?: (page: number) => void
    pageSize?: number
    total?: number
    current?: number
  }
}

export function MovieList({ title, movies, pagination, onAdd, onDelete, onChange }: MovieListProps) {

  return (
    <Container>
      {title && <h2>{title}</h2>}
      <List
        itemLayout="vertical"
        size="large"
        grid={{
          gutter: 20,
          xs: 1,
          sm: 2,
          md: 4,
          lg: 4,
          xl: 6,
          xxl: 10,
        }}
        pagination={{
          onChange: pagination?.onChange,
          pageSize: pagination?.pageSize || 10,
          total: pagination?.total,
          current: pagination?.current,
          showSizeChanger: false,
        }}
        dataSource={movies}
        renderItem={(item) => {
          const Wrapper = item.watched || item.saved ? Badge.Ribbon : React.Fragment
          let props
          if (item.watched) {
            props = { color: 'green', text: 'Watched ðŸŽ‰' }
          } else if (item.saved) {
            props = { color: 'cyan', text: 'Movie added to your watch list' }
          }

          return (
            <Wrapper {...props}>
              <MovieContainer>
                <Cover>
                  {getCover(item)}
                </Cover>
                {dayjs(item.release_date).format('MMM YYYY')}
                <ActionButtons item={item} onAdd={onAdd} onChange={onChange} onDelete={onDelete} />
              </MovieContainer>
            </Wrapper>
          )
        }}
      />
    </Container>
  )
}

const Container = styled.div`
  margin: 1rem;
`

const MovieContainer = styled.div`
  margin: 1rem 1rem;
  display: flex;
  flex-direction: column;
  padding: 0.5rem;
  border-radius: 5px;
  // cursor: pointer;

  &:hover {
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
  }
`

const Cover = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
`

function getCover(item: EnhancedMovie) {
  if (item.poster_path) {
    return (
      <Image
        width={150}
        alt="logo"
        src={`${process.env.REACT_APP_IMG_URL}w200/${item.poster_path}`}
        preview={false}
      />
    )
  }
  return (
    <div>
      <Image
        width={150}
        height={200}
        src="error"
        preview={false}
        fallback="data:image/png;base64,"
      />
      <Card.Meta title={item.title} />
    </div>
  )
}
